mapper.select:
SELECT name_ko
FROM cms__board
WHERE board_id = #{boardId};

SELECT *
FROM cms__article
WHERE board_id = #{boardId}
  AND article_id = #{articleId};

mapper.update:
UPDATE cms__article
SET view_count = view_count + 1
WHERE board_id = #{boardId} AND article_id = #{articleId}

mapper.delete:
DELETE
FROM cms__article
WHERE board_id = #{boardId} AND article_id = #{articleId}
